import { createSlice } from '@reduxjs/toolkit';

const dslSlice = createSlice({
    name: 'dsl',
    initialState: {
        code: '',
        output: '',
    },
    reducers: {
        interpret: (state, action) => {
            const code = action.payload;
            state.code = code;

            // Lógica para interpretar o código e gerar a saída
            if (code.startsWith('PRINT')) {
                state.output = code.slice(6); // Supondo que "PRINT " é o comando
            } else {
                state.output = 'Comando desconhecido';
            }
        },
    },
});

export const { interpret } = dslSlice.actions;
export default dslSlice.reducer;
